function slickSlider(){$(".slider").slick({infinite:!0,autoplay:!0,arrows:!0,dots:!0})}function magnificPopup(){$(".lightbox").magnificPopup({type:"image",gallery:{enabled:!0,tPrev:"Vorheriges (Linke Pfeiltaste)",tNext:"Nächstes (Rechte Pfeiltaste)",tCounter:"%curr% von %total%"},tClose:"Schließen (Esc)",tLoading:"Lädt",image:{tError:'<a href="%url%">Das Bild</a> konnte nicht geladen werden.'},ajax:{tError:'<a href="%url%">Die Anfrage</a> ist fehlgeschlagen.'}})}function mmenu(){$("#menu").mmenu()}function isotopes(){$(".grid").isotope({itemSelector:".grid-item",layoutMode:"fitRows"})}function clipboardJS(){new Clipboard(".btn")}function info(){swal({title:"Information",type:"info",html:'Version: 3.0.0-alpha <br>Developer: Julian Seidl <br><br>Github: <a href="https://github.com/Thejuse/CMS-Installer">CMS-Installer</a> <br>Web: <a href="https://www.installer.jseidl.at">www.installer.jseidl.at</a>',showCloseButton:!0})}function cookieConsent(){window.addEventListener("load",function(){window.cookieconsent.initialise({palette:{popup:{background:"#212121",text:"#ffffff"},button:{background:"#606060",text:"#ffffff"}},showLink:!1,theme:"edgeless",content:{message:"Diese Webseite verwendet Cookies, um die Bedienfreundlichkeit zu erhöhen.",dismiss:"OK"}})})}function masonry(){$(".grid").masonry({itemSelector:".grid-item",columnWidth:200})}function scrollDown(){$(".scroll-trigger").click(function(){return $("html, body").animate({scrollTop:$(this).offset().top+"px"},"slow"),this})}$(document).ready(function(){scrollDown()});